func userInitials(name string) string {
	if name == "" {
		return "U"
	}
	rs := []rune(name)
	if len(rs) == 1 {
		return strings.ToUpper(string(rs[0:1]))
	}
	return strings.ToUpper(string(rs[0:1]))
}

